#pragma mark - Table view data source

- (NSInteger)numberOfSectionsInTableView:(UITableView *)tableView
{
    // Return the number of sections.
    return 1;
}

- (NSInteger)tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section
{

    // Return the number of rows in the section.
    return [books count];
}

- (UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath
{

    //UITableViewCell *cell = [tableView dequeueReusableCellWithIdentifier:@"BookCell" forIndexPath:indexPath];
    HomeBookTableViewCell *cell = (HomeBookTableViewCell *)[tableView dequeueReusableCellWithIdentifier:@"BookCell" forIndexPath:indexPath];
 
    Book *book = [self.books objectAtIndex:indexPath.row];
   /* UILabel *titleLabel = (UILabel *)[cell viewWithTag:101];
    UILabel *authorLabel = (UILabel *)[cell viewWithTag:102];
    UIImageView *imageImageView = (UIImageView *)[cell viewWithTag:100];
	titleLabel.text = book.title;
	authorLabel.text = book.author;
    imageImageView.image = book.image;*/
    
    cell.titleLabel.text = book.title;
    cell.titleLabel.numberOfLines = 0;
    [cell.titleLabel sizeToFit];
	cell.authorLabel.text = book.author;
    cell.imageView.image = book.image;
    cell.text = book.text;
    [cell.progressView setProgress:book.readed];
    
    return cell;
}

